import React from 'react';
import { Bar  } from 'react-chartjs-2';


const BarChart = () => {
    return <div>
        <Bar
            data={{
               labels: Array('Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'),
        datasets: [{
            label: '# of Votes',
            data: [12, 19, 3, 5, 2, 3],
            backgroundColor: [
                        'rgba(255, 99, 132, 0.2)',
                        'rgba(54, 162, 235, 0.2)',
                        'rgba(255, 206, 86, 0.2)',
                        'rgba(75, 192, 192, 0.2)',
                        'rgba(153, 102, 255, 0.2)',
                        'rgba(255, 159, 64, 0.2)'
                    ],
                    borderColor: [
                        'rgba(255, 99, 132, 1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)',
                        'rgba(75, 192, 192, 1)',
                        'rgba(153, 102, 255, 1)',
                        'rgba(255, 159, 64, 1)'
                    ],
                    borderWidth: 1,
                },
                {
                    label: '# Quantity',
                    data: [47, 104, 67, 58, 22, 50],
                    backgroundColor: [
                        'rgba(255, 99, 132, 1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)',
                        'rgba(75, 192, 192, 1)',
                        'rgba(153, 102, 255, 1)',
                        'rgba(255, 159, 64, 1)'
                    ],
                    borderColor: 'red',
                }
            ]
             }}
            width={350}
            height={150}
            options={{ 
                scales: {
                    y: {
                        beginAtZero: true
                    }
                }
              }}
        /> 
    </div>
}

export default BarChart;
